import React from "react";

export default function ProjectCard({ project, deleteProject }) {
  const handleDownload = async (file) => {
    if (!file) return;

    try {
      const response = await fetch(
        `${process.env.REACT_APP_API_URL}/projects/download/${encodeURIComponent(file)}`
      );
      if (!response.ok) return alert("File not found");

      const blob = await response.blob();
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = file; // keeps original filename
      link.click();

      URL.revokeObjectURL(url);
    } catch (err) {
      console.error(err);
      alert("Error downloading file");
    }
  };

  return (
    <div
      style={{
        border: "1px solid #ccc",
        padding: "10px",
        margin: "8px",
        borderRadius: "5px",
        backgroundColor: "#fff",
      }}
    >
      <strong>{project.name}</strong> ({project.status})<br />
      <em>{project.role}</em> | {project.industry} | Team: {project.team_size}<br />
      {project.start_date} - {project.end_date || "Present"}<br />
      <p>{project.description}</p>
      <p>Technologies: {project.technologies?.join(", ")}</p>
      {project.project_url && (
        <a href={project.project_url} target="_blank" rel="noreferrer">
          Project Link
        </a>
      )}
      {project.achievements && <p>Achievements: {project.achievements}</p>}

      {project.media_files?.length > 0 && (
        <div>
          Media:{" "}
          {project.media_files.map((file, idx) => (
            <span
              key={idx}
              style={{
                color: "blue",
                textDecoration: "underline",
                cursor: "pointer",
                marginRight: "5px",
              }}
              onClick={() => handleDownload(file)}
            >
              {file}
            </span>
          ))}
        </div>
      )}

      <div style={{ marginTop: "5px" }}>
        <button onClick={() => deleteProject(project.id)}>ðŸ—‘ Delete</button>
      </div>
    </div>
  );
}
