import React, { useState } from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({
  category,
  skills,
  updateSkill,
  removeSkill,
  categoryFilters,
  setCategoryFilters,
}) {
  const [levelSummary, setLevelSummary] = useState({});

  // Apply category-specific search/filter
  const catSearchTerm = categoryFilters[category] || "";
  const filteredSkills = skills.filter((s) =>
    s.name.toLowerCase().includes(catSearchTerm.toLowerCase())
  );

  // Compute proficiency summary for filtered skills
  filteredSkills.forEach((s) => {
    levelSummary[s.proficiency] = (levelSummary[s.proficiency] || 0) + 1;
  });

  // Export function for this category
  const exportCategory = () => {
    let text = `${category} (${filteredSkills.length})\n`;
    text += Object.entries(levelSummary)
      .map(([level, count]) => `${level}: ${count}`)
      .join(", ") + "\n\n";
    filteredSkills.forEach((s) => {
      text += `• ${s.name} - ${s.proficiency}\n`;
    });
    const blob = new Blob([text], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `${category}_skills.txt`;
    link.click();
  };

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px",
      }}
    >
      <h3>
        {category} ({filteredSkills.length})
        <button onClick={exportCategory} style={{ marginLeft: "10px" }}>
          Export
        </button>
      </h3>

      {/* Category filter input */}
      <input
        placeholder={`Filter ${category} skills...`}
        value={catSearchTerm}
        onChange={(e) =>
          setCategoryFilters({ ...categoryFilters, [category]: e.target.value })
        }
        style={{ marginBottom: "8px" }}
      />

      {/* Category-level proficiency summary */}
      <p>
        {Object.entries(levelSummary)
          .map(([level, count]) => `${level}: ${count}`)
          .join(", ")}
      </p>

      <SortableContext
        items={filteredSkills.length ? filteredSkills.map((s) => s.id) : [`empty-${category}`]} // placeholder id for empty lists
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {filteredSkills.length ? (
            filteredSkills.map((skill) => (
              <SortableItem
                key={skill.id}
                skill={skill}
                updateSkill={updateSkill}
                removeSkill={removeSkill}
              />
            ))
          ) : (
            <li
              style={{
                padding: "20px 0",
                color: "#aaa",
                textAlign: "center",
              }}
            >
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
