import React, { useState } from "react";

export default function ProjectForm({ addProject }) {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [role, setRole] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [skillsUsed, setSkillsUsed] = useState("");
  const [industryType, setIndustryType] = useState("");
  const [status, setStatus] = useState("Planned");
  const [files, setFiles] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("role", role);
    formData.append("start_date", startDate);
    if (endDate) formData.append("end_date", endDate);
    if (skillsUsed) formData.append("skills_used", skillsUsed);
    if (industryType) formData.append("industry_type", industryType);
    formData.append("status", status);

    // Append all selected files correctly
    for (let i = 0; i < files.length; i++) {
      formData.append("files", files[i]);
    }

    addProject(formData);

    // Reset form
    setName(""); setDescription(""); setRole(""); setStartDate(""); setEndDate("");
    setSkillsUsed(""); setIndustryType(""); setStatus("Planned"); setFiles([]);
    // Reset file input display
    document.getElementById("project-files").value = "";
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "16px" }}>
      <input placeholder="Project Name" value={name} onChange={e => setName(e.target.value)} required />
      <input placeholder="Role" value={role} onChange={e => setRole(e.target.value)} required />
      <textarea placeholder="Description" value={description} onChange={e => setDescription(e.target.value)} required />
      <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} required />
      <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
      <input placeholder="Skills Used (comma-separated)" value={skillsUsed} onChange={e => setSkillsUsed(e.target.value)} />
      <input placeholder="Industry Type" value={industryType} onChange={e => setIndustryType(e.target.value)} />
      <select value={status} onChange={e => setStatus(e.target.value)}>
        <option value="Planned">Planned</option>
        <option value="Ongoing">Ongoing</option>
        <option value="Completed">Completed</option>
      </select>

      <input
        type="file"
        id="project-files"
        multiple
        onChange={(e) => setFiles(Array.from(e.target.files))}
      />

      <button type="submit">Add Project</button>
    </form>
  );
}
