import React from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import SkillItem from "./SkillItem";

export function SortableItem({ skill, updateSkill, removeSkill, isPlaceholder }) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: skill.id });

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: skill.isDummy ? 0.4 : 1,
    cursor: skill.isDummy ? "default" : "grab",
    pointerEvents: skill.isDummy ? "none" : "auto",
    marginBottom: "4px"
  };

  return (
    <li ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <SkillItem skill={skill} updateSkill={updateSkill} removeSkill={removeSkill} />
    </li>
  );
}
