import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date().toISOString().split("T")[0];

  const expired = cert.expiration_date && cert.expiration_date < today;
  const expiringSoon = cert.expiration_date && !cert.does_not_expire &&
                       new Date(cert.expiration_date) <= new Date(Date.now() + 90*24*60*60*1000);

  let bgColor = "#fff";
  let warningText = "";
  if (expired) {
    bgColor = "#a43333ff"; // soft red
    warningText = "‚ùå Expired";
  } else if (expiringSoon) {
    bgColor = "#e08f38ff"; // soft orange
    warningText = "‚ö†Ô∏è About to Expire";
  }

  return (
    <div style={{
      border: "1px solid #ccc",
      borderRadius: "4px",
      padding: "8px",
      marginBottom: "8px",
      backgroundColor: bgColor
    }}>
      {(expired || expiringSoon) && <div style={{ fontWeight: "bold", marginBottom: "4px" }}>{warningText}</div>}

      <strong>{cert.name}</strong> ({cert.category || "Categories"})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />
      {cert.document_url && (
        <a 
          href={`http://127.0.0.1:8000/uploads/certifications/${cert.document_url}`} 
          download
        >
          üìÑ Download Document
        </a>
      )}
      <br />
      <button 
        style={{ marginTop: "6px", backgroundColor: "#ccc", border: "none", padding: "4px 8px", cursor: "pointer" }} 
        onClick={() => onDelete(cert.id)}
      >
        üóë Delete
      </button>
    </div>
  );
}
