import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expDate = cert.expiration_date ? new Date(cert.expiration_date) : null;

  let style = { border: "1px solid #ccc", borderRadius: "4px", padding: "8px", marginBottom: "8px" };
  let warningText = "";

  if (!cert.does_not_expire && expDate) {
    const diffDays = Math.ceil((expDate - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) { style.backgroundColor = "#82272fff"; warningText = "‚ùå EXPIRED"; }
    else if (diffDays <= 90) { style.backgroundColor = "#cfb665ff"; warningText = "‚ö†Ô∏è About to expire"; }
  }

  return (
    <div style={style}>
      {warningText && <div style={{ fontWeight: "bold", marginBottom: "4px" }}>{warningText}</div>}
      <strong>{cert.name}</strong> ({cert.category})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />
      {cert.document_url && (
        <a href={`/uploads/certifications/${cert.document_url}`} download target="_blank" rel="noreferrer">
          üìÑ Download Document
        </a>
      )}
      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
