import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({
  category,
  skills,
  updateSkill,
  removeSkill,
  filterTerm = "",
  exportCategory, // function passed from parent for exporting
}) {
  // Filter skills within this category by filterTerm
  const filteredSkills = skills.filter((s) =>
    s.name.toLowerCase().includes(filterTerm.toLowerCase())
  );

  // Count proficiency levels for summary
  const levelSummary = filteredSkills.reduce((acc, s) => {
    acc[s.proficiency] = (acc[s.proficiency] || 0) + 1;
    return acc;
  }, {});

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px",
      }}
    >
      <h3>
        {category} ({filteredSkills.length})
        {exportCategory && (
          <button onClick={exportCategory} style={{ marginLeft: "10px" }}>
            Export
          </button>
        )}
      </h3>

      {Object.keys(levelSummary).length > 0 && (
        <p>
          {Object.entries(levelSummary)
            .map(([level, count]) => `${level}: ${count}`)
            .join(", ")}
        </p>
      )}

      <SortableContext
        items={skills.length ? skills.map((s) => s.id) : [`empty-${category}`]} // placeholder ID for empty
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {filteredSkills.length ? (
            filteredSkills.map((skill) => (
              <SortableItem
                key={skill.id}
                skill={skill}
                updateSkill={updateSkill}
                removeSkill={removeSkill}
              />
            ))
          ) : (
            <li
              style={{
                padding: "20px 0",
                color: "#aaa",
                textAlign: "center",
              }}
            >
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
