import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill }) {
  // Count summary by proficiency
  const levelSummary = skills.reduce((acc, s) => {
    acc[s.proficiency] = (acc[s.proficiency] || 0) + 1;
    return acc;
  }, {});

  // Export handler
  const exportCategory = () => {
    let text = `${category} (${skills.length})\n`;
    text += Object.entries(levelSummary)
      .map(([level, count]) => `${level}: ${count}`)
      .join(", ") + "\n\n";
    skills.forEach(s => {
      text += `• ${s.name} - ${s.proficiency}\n`;
    });

    const blob = new Blob([text], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `${category}_skills.txt`;
    link.click();
  };

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px"
      }}
    >
      <h3>
        {category} ({skills.length})
        <button onClick={exportCategory} style={{ marginLeft: "10px" }}>
          Export
        </button>
      </h3>

      {Object.keys(levelSummary).length > 0 && (
        <p>{Object.entries(levelSummary).map(([level, count]) => `${level}: ${count}`).join(", ")}</p>
      )}

      <SortableContext
        items={skills.length ? skills.map(s => s.id) : [`empty-${category}`]}
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length ? (
            skills.map(skill => (
              <SortableItem
                key={skill.id}
                skill={skill}
                updateSkill={updateSkill}
                removeSkill={removeSkill}
              />
            ))
          ) : (
            <li style={{ padding: "20px 0", color: "#aaa", textAlign: "center" }}>
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
