import React, { useState } from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill, activeId }) {
  const [isOver, setIsOver] = useState(false);

  // Dummy item to allow drop on empty list
  const items = skills.length ? skills.map(s => s.id) : [`dummy-${category}`];

  return (
    <div
      onDragOver={() => setIsOver(true)}
      onDragLeave={() => setIsOver(false)}
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px",
        backgroundColor: isOver ? "#e0f7fa" : "white" // highlight on hover
      }}
    >
      <h3>{category} ({skills.length})</h3>

      <SortableContext items={items} strategy={verticalListSortingStrategy}>
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length ? (
            skills.map(skill => (
              <SortableItem
                key={skill.id}
                skill={skill}
                updateSkill={updateSkill}
                removeSkill={removeSkill}
              />
            ))
          ) : (
            <li style={{ padding: "20px 0", color: "#aaa", textAlign: "center" }}>
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
