import React from "react";

export default function CertificationCard({ cert, onDelete, onToggleVerified }) {
  const today = new Date().toISOString().split("T")[0];
  const expired = cert.expired;
  const renewalSoon = cert.needs_renewal;

  return (
    <div style={{
      border: "1px solid #ccc",
      borderRadius: "4px",
      padding: "8px",
      marginBottom: "8px"
    }}>
      <strong>{cert.name}</strong> ({cert.category})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span>
      <button onClick={() => onToggleVerified(cert)}>Toggle</button><br />
      {renewalSoon && <span style={{ color: "orange" }}>Renewal Soon</span>}<br />
      {expired && <span style={{ color: "red" }}>Expired</span>}<br />
      {cert.document_url && (
        <a
          href={`${process.env.REACT_APP_API_URL}/uploads/certifications/${cert.document_url}`}
          target="_blank"
          rel="noreferrer"
        >
          üìÑ View Document
        </a>
      )}<br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
