import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expiration = cert.expiration_date ? new Date(cert.expiration_date) : null;

  let status = "";
  let bgColor = "#fff";

  if (expiration) {
    const diffDays = Math.ceil((expiration - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) {
      status = "‚ùå EXPIRED";
      bgColor = "#ff4d4d";
    } else if (diffDays <= 90) {
      status = "‚ö†Ô∏è About to expire";
      bgColor = "#fff3cd";
    }
  }

  return (
    <div style={{ border: "1px solid #ccc", borderRadius: "4px", padding: "8px", marginBottom: "8px", backgroundColor: bgColor }}>
      {status && <div style={{ fontWeight: "bold", marginBottom: "4px" }}>{status}</div>}
      <strong>{cert.name}</strong> ({cert.category})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />
      {cert.document_url && (
        <a
          href={`http://127.0.0.1:8000/uploads/certifications/${cert.document_url}`}
          download
        >
          üìÑ Download Document
        </a>
      )}
      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
