import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill }) {
  // If empty, use a dummy skill
  const items = skills.length ? skills.map(s => s.id) : [`dummy-${category}`];

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px"
      }}
    >
      <h3>{category} ({skills.length})</h3>

      <SortableContext
        items={items}
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length ? (
            skills.map(skill => (
              <SortableItem
                key={skill.id}
                skill={skill}
                updateSkill={updateSkill}
                removeSkill={removeSkill}
              />
            ))
          ) : (
            <SortableItem
              key={`dummy-${category}`}
              skill={{ id: `dummy-${category}`, name: "Drop skills here", category, isDummy: true }}
              updateSkill={() => {}}
              removeSkill={() => {}}
              isPlaceholder={true}
            />
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
