import React from "react";

const API_URL = process.env.REACT_APP_API_URL;

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expDate = cert.expiration_date ? new Date(cert.expiration_date) : null;
  const expiringSoon = expDate && (expDate - today <= 1000 * 60 * 60 * 24 * 90);
  const expired = expDate && expDate < today;

  const bgColor = expired
    ? "#a43333ff"
    : expiringSoon
    ? "#e08f38ff"
    : "#fff";

  const warningText = expired
    ? "‚ùå Expired"
    : expiringSoon
    ? "‚ö†Ô∏è Expiring Soon"
    : "";

  const handleDownload = async () => {
    if (!cert.has_document) return;

    const response = await fetch(
      `${API_URL}/certifications/download/${cert.id}`
    );

    if (!response.ok) return alert("File not found");

    const blob = await response.blob();
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = cert.document_name || "document";
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ backgroundColor: bgColor, borderRadius: "4px", padding: "8px", marginBottom: "8px" }}>
      {warningText && <div style={{ fontWeight: "bold", marginBottom: "4px" }}>{warningText}</div>}
      <strong>{cert.name}</strong>
      <br />
      {cert.issuer} | Earned: {cert.date_earned}
      <br />
      {!cert.does_not_expire && (
        <span>Expires: {cert.expiration_date || "-"}</span>
      )}
      <br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>
        {cert.verified ? "‚úÖ" : "‚ùå"}
      </span>
      <br />

      {cert.has_document && (
        <span
          style={{
            color: "blue",
            textDecoration: "underline",
            cursor: "pointer",
            marginTop: "4px",
            display: "inline-block",
          }}
          onClick={handleDownload}
        >
          üìÑ Download Document
        </span>
      )}

      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
