import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill, activeId }) {
  return (
    <div style={{ marginBottom: "20px", minHeight: "60px", border: "1px dashed #ccc", padding: "10px", borderRadius: "8px" }}>
      <h3>{category} ({skills.length})</h3>

      <SortableContext
        items={skills.length ? skills.map(s => s.id) : [`empty-${category}`]}
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length ? (
            skills.map(skill =>
              skill.id === activeId ? null : ( // hide active while dragging
                <SortableItem
                  key={skill.id}
                  skill={skill}
                  updateSkill={updateSkill}
                  removeSkill={removeSkill}
                />
              )
            )
          ) : (
            <li style={{ padding: "20px 0", color: "#aaa", textAlign: "center" }}>
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
