import React from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import SkillItem from "./SkillItem";

export function SortableItem({ skill, updateSkill, removeSkill, activeId }) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: skill.id });

  // Hide the dragged item but keep its space
  const isDragging = activeId === skill.id;

  const style = {
    transform: transform ? CSS.Transform.toString(transform) : undefined,
    transition,
    marginBottom: "5px",
    opacity: isDragging ? 0 : 1,
    pointerEvents: isDragging ? "none" : "auto",
  };

  return (
    <li ref={setNodeRef} style={style} {...attributes} {...listeners}>
      <SkillItem
        skill={skill}
        updateSkill={updateSkill}
        removeSkill={removeSkill}
      />
    </li>
  );
}
