import React from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import SkillItem from "./SkillItem"

export default function SkillCategory({ category, skills, updateSkill, removeSkill }) {
  // If no skills, create a dummy sortable item for the drop target
  const items = skills.length ? skills.map(s => s.id) : [`empty-${category}`];

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px"
      }}
    >
      <h3>{category} ({skills.length})</h3>

      <SortableContext items={items} strategy={verticalListSortingStrategy}>
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length
            ? skills.map(skill => (
                <SortableItem
                  key={skill.id}
                  skill={skill}
                  updateSkill={updateSkill}
                  removeSkill={removeSkill}
                />
              ))
            : (
              <SortableItem
                key={`empty-${category}`}
                skill={{ id: `empty-${category}`, name: "Drop skills here", category }}
                updateSkill={() => {}}
                removeSkill={() => {}}
                isPlaceholder={true} // we can use this flag to style it differently
              />
            )
          }
        </ul>
      </SortableContext>
    </div>
  );
}
