import React, { useState } from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill }) {
  const [filterTerm, setFilterTerm] = useState("");

  // Filtered skills for visual display
  const filteredSkills = skills.filter((s) =>
    s.name.toLowerCase().includes(filterTerm.toLowerCase())
  );

  // Proficiency summary for the full category
  const proficiencySummary = skills.reduce((acc, s) => {
    acc[s.proficiency] = (acc[s.proficiency] || 0) + 1;
    return acc;
  }, {});

  const exportCategory = () => {
    let text = `${category} (${skills.length})\n`; // full category count
    text += Object.entries(proficiencySummary)
      .map(([level, count]) => `${level}: ${count}`)
      .join(", ") + "\n\n";

    skills.forEach((s) => {
      text += `• ${s.name} - ${s.proficiency}\n`;
    });

    const blob = new Blob([text], { type: "text/plain" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `${category}_skills.txt`;
    link.click();
  };

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px",
      }}
    >
      <h3>
        {category} ({skills.length})
        <button onClick={exportCategory} style={{ marginLeft: "10px" }}>
          Export
        </button>
      </h3>

      <input
        placeholder={`Filter ${category} skills...`}
        value={filterTerm}
        onChange={(e) => setFilterTerm(e.target.value)}
        style={{ marginBottom: "8px", width: "100%" }}
      />

      <p>
        {Object.entries(proficiencySummary)
          .map(([level, count]) => `${level}: ${count}`)
          .join(", ")}
      </p>

      <SortableContext
        items={skills.map((s) => s.id)} // always use full category for DnD
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {skills.length ? (
            skills.map((skill) => {
              const hidden = !skill.name.toLowerCase().includes(filterTerm.toLowerCase());
              return (
                <SortableItem
                  key={skill.id}
                  skill={skill}
                  updateSkill={updateSkill}
                  removeSkill={removeSkill}
                  style={{ display: hidden ? "none" : "flex" }}
                />
              );
            })
          ) : (
            <li style={{ padding: "20px 0", color: "#aaa", textAlign: "center" }}>
              Drop skills here
            </li>
          )}
        </ul>
      </SortableContext>
    </div>
  );
}
