import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill, activeId }) {
  const filteredSkills = skills.filter(s => s.id !== activeId);

  return (
    <div style={{
      marginBottom: "20px",
      border: "1px solid #ccc",
      borderRadius: "8px",
      padding: "10px",
      minHeight: "50px", // Always show some space
      backgroundColor: "#f9f9f9"
    }}>
      <h3>{category} ({filteredSkills.length})</h3>

      <SortableContext items={filteredSkills.map(s => s.id)} strategy={verticalListSortingStrategy}>
        {filteredSkills.length > 0 ? (
          filteredSkills.map(skill => (
            <SortableItem
              key={skill.id}
              skill={skill}
              updateSkill={updateSkill}
              removeSkill={removeSkill}
            />
          ))
        ) : (
          <div style={{
            fontStyle: "italic",
            color: "#888",
            padding: "4px 0"
          }}>
            No skills yet
          </div>
        )}
      </SortableContext>
    </div>
  );
}
