import React from "react";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { SortableItem } from "./SortableItem";

export default function SkillCategory({ category, skills, updateSkill, removeSkill }) {
  // Add a dummy skill for empty categories so DnD can accept drops
  const displaySkills = skills.length
    ? skills
    : [{ id: `dummy-${category}`, name: "Drop skills here", category, proficiency: "", isDummy: true }];

  return (
    <div
      style={{
        marginBottom: "20px",
        minHeight: "60px",
        border: "1px dashed #ccc",
        padding: "10px",
        borderRadius: "8px"
      }}
    >
      <h3>{category} ({skills.length})</h3>

      <SortableContext
        items={displaySkills.map(s => s.id)}
        strategy={verticalListSortingStrategy}
      >
        <ul style={{ padding: 0, listStyle: "none", minHeight: "40px" }}>
          {displaySkills.map(skill => (
            <SortableItem
              key={skill.id}
              skill={skill}
              updateSkill={updateSkill}
              removeSkill={removeSkill}
            />
          ))}
        </ul>
      </SortableContext>
    </div>
  );
}
