import React from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import SkillItem from "./SkillItem"

export function SortableItem({ skill, updateSkill, removeSkill, isPlaceholder }) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: skill.id });

  const style = {
    transform: transform ? `translate3d(${transform.x}px, ${transform.y}px, 0)` : undefined,
    transition,
    marginBottom: "5px",
    opacity: isPlaceholder ? 0.3 : 1,
    pointerEvents: isPlaceholder ? "none" : "auto", // prevent interacting with placeholder
  };

  return (
    <li ref={setNodeRef} style={style} {...attributes} {...listeners}>
      {!isPlaceholder && (
        <SkillItem
          skill={skill}
          updateSkill={updateSkill}
          removeSkill={removeSkill}
          listeners={listeners}
          attributes={attributes}
          setNodeRef={setNodeRef}
          transform={transform}
          transition={transition}
        />
      )}
      {isPlaceholder && <div style={{ textAlign: "center", color: "#aaa" }}>{skill.name}</div>}
    </li>
  );
}
