import React from "react";

export default function CertificationCard({ cert, onDelete, onToggleVerified }) {
  const today = new Date().toISOString().split("T")[0];
  const expired = cert.expiration_date && !cert.does_not_expire && cert.expiration_date < today;

  return (
    <div style={{ border: "1px solid #ccc", padding: "8px", marginBottom: "8px", borderRadius: "6px" }}>
      <strong>{cert.name}</strong>{cert.category ? ` ‚Äî ${cert.category}` : ""}<br />
      {cert.issuer} | Earned: {cert.date_earned}
      {" | "}
      {cert.does_not_expire ? "Does Not Expire" : `Expires: ${cert.expiration_date || "-"}`}
      {expired && <span style={{ color: "red" }}> (Expired)</span>}
      <br />

      Verified:{" "}
      <span style={{ color: cert.verified ? "green" : "red" }}>
        {cert.verified ? "‚úÖ" : "‚ùå"}
      </span>
      <button style={{ marginLeft: "6px" }} onClick={() => onToggleVerified(cert)}>Toggle</button><br />

      {cert.cert_id && <span>Certification ID: {cert.cert_id}</span>}<br />

      {cert.document_url && (
        <a href={`http://127.0.0.1:8000/uploads/certifications/${cert.document_url}`}
           target="_blank" rel="noreferrer">
          üìÑ View Document
        </a>
      )}
      <br />

      <button style={{ marginTop: "6px" }} onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
