import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expirationDate = cert.expiration_date ? new Date(cert.expiration_date) : null;
  const daysToExpire = expirationDate ? Math.ceil((expirationDate - today) / (1000 * 60 * 60 * 24)) : null;

  let cardStyle = {
    border: "1px solid #ccc",
    borderRadius: "4px",
    padding: "12px",
    marginBottom: "12px",
    backgroundColor: "white",
    position: "relative"
  };

  let warningText = "";
  if (!cert.does_not_expire && expirationDate) {
    if (daysToExpire < 0) {
      cardStyle.backgroundColor = "#f8d7da"; // red
      cardStyle.borderColor = "#f5c2c7";
      warningText = "‚ùå EXPIRED";
    } else if (daysToExpire <= 90) {
      cardStyle.backgroundColor = "#fff3cd"; // yellow
      cardStyle.borderColor = "#ffecb5";
      warningText = "‚ö†Ô∏è About to expire";
    }
  }

  return (
    <div style={cardStyle}>
      {/* Warning on top */}
      {warningText && (
        <div style={{ fontWeight: "bold", marginBottom: "8px" }}>{warningText}</div>
      )}

      <strong>{cert.name}</strong> ({cert.category || "-"})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />

      {/* Download link for document */}
      {cert.document_url && (
        <a
          href={`${process.env.REACT_APP_API_URL}/uploads/certifications/${cert.document_url}`}
          download
          style={{ textDecoration: "underline", color: "#0d6efd" }}
        >
          üìÑ Download Document
        </a>
      )}
      <br />

      <button onClick={() => onDelete(cert.id)} style={{ marginTop: "8px" }}>üóë Delete</button>
    </div>
  );
}
