import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expired = cert.expiration_date && new Date(cert.expiration_date) < today;
  const expiringSoon = cert.expiration_date && !expired && (new Date(cert.expiration_date) - today) / (1000*60*60*24) <= 90;

  // Decide background color
  let bgColor = "#fff"; // default
  let warningText = "";
  if (expired) {
    bgColor = "#a43333ff"; // softer red
    warningText = "‚ùå Expired";
  } else if (expiringSoon) {
    bgColor = "#ffa94d"; // soft orange
    warningText = "‚ö†Ô∏è About to expire";
  }

  return (
    <div style={{
      border: "1px solid #ccc",
      borderRadius: "4px",
      padding: "12px",
      marginBottom: "12px",
      backgroundColor: bgColor,
      position: "relative"
    }}>
      {warningText && (
        <div style={{
          fontWeight: "bold",
          marginBottom: "6px"
        }}>{warningText}</div>
      )}

      <strong>{cert.name}</strong> ({cert.category})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />
      {cert.document_url && (
        <a href={`http://127.0.0.1:8000/uploads/certifications/${cert.document_url}`} download>
          üìÑ Download Document
        </a>
      )}
      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
