import React from "react";

export default function CertificationCard({ cert, onDelete }) {
  const today = new Date();
  const expiration = cert.expiration_date ? new Date(cert.expiration_date) : null;

  // Determine warning status
  let warning = null;
  if (!cert.does_not_expire && expiration) {
    const diffDays = Math.ceil((expiration - today) / (1000 * 60 * 60 * 24));
    if (diffDays < 0) warning = { color: "red", text: "‚ùå Expired" };
    else if (diffDays <= 90) warning = { color: "orange", text: "‚ö†Ô∏è Expiring Soon" };
  }

  const boxStyle = {
    border: "1px solid #ccc",
    borderRadius: "4px",
    padding: "8px",
    marginBottom: "8px",
    backgroundColor: warning ? warning.color : "#fff",
    color: warning ? "#fff" : "#000",
  };

  return (
    <div style={boxStyle}>
      {warning && <div style={{ fontWeight: "bold", marginBottom: "4px" }}>{warning.text}</div>}

      <strong>{cert.name}</strong> ({cert.category})<br />
      {cert.issuer} | Earned: {cert.date_earned} | {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert #: {cert.cert_id}</span>}<br />
      Verified: <span style={{ color: cert.verified ? "green" : "red" }}>{cert.verified ? "‚úÖ" : "‚ùå"}</span><br />

      {cert.document_url && (
        <a
          href={`${process.env.REACT_APP_API_URL}/uploads/certifications/${cert.document_url}`}
          download
        >
          üìÑ Download Document
        </a>
      )}
      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
    </div>
  );
}
