import React from "react";

export default function CertificationCard({ cert, onDelete, onToggleVerified }) {
  const today = new Date().toISOString().split("T")[0];
  const expired = cert.expiration_date && cert.expiration_date < today;

  return (
    <div style={{ border: "1px solid #ccc", borderRadius: "4px", padding: "8px", marginBottom: "8px" }}>
      <strong>{cert.name}</strong> {cert.category ? `(${cert.category})` : ""}<br />
      {cert.issuer} | Earned: {cert.date_earned}<br />
      {cert.does_not_expire ? "Does not expire" : `Expires: ${cert.expiration_date || "-"}`}<br />
      {cert.cert_id && <span>Cert ID: {cert.cert_id}</span>}<br />
      Verified:{" "}
      <span style={{ color: cert.verified ? "green" : "red" }}>
        {cert.verified ? "‚úÖ" : "‚ùå"}
      </span>
      <button onClick={() => onToggleVerified(cert)}>Toggle</button><br />
      {cert.document_url && (
        <a href={`http://127.0.0.1:8000/uploads/certifications/${cert.document_url}`} target="_blank" rel="noreferrer">
          View Document
        </a>
      )}
      <br />
      <button onClick={() => onDelete(cert.id)}>üóë Delete</button>
      {expired && <span style={{ color: "red", marginLeft: "8px" }}>Expired</span>}
    </div>
  );
}
