import React, { useState } from "react";

const API_URL = process.env.REACT_APP_API_URL + "/projects";

export default function ProjectCard({ project, deleteProject }) {
  const [expanded, setExpanded] = useState(false);

  const isImage = (filename) =>
    /\.(png|jpe?g|gif|webp)$/i.test(filename);

  const fileUrl = (filename) =>
    `${API_URL}/download/${project.id}/${encodeURIComponent(filename)}?user_id=temp_user`;

  return (
    <div className="project-card" style={{ border:"1px solid #ccc", padding:"10px", margin:"8px", borderRadius:"5px" }}>
      <div onClick={() => setExpanded(!expanded)} style={{ cursor:"pointer" }}>
        <strong>{project.name}</strong> â€” {project.status}<br/>
        <em>{project.role}</em><br/>
        {project.start_date} - {project.end_date || "Present"}<br/>
      </div>

      {expanded && (
        <div>
          <p>{project.description}</p>

          {project.media_files?.length > 0 && (
            <div>
              {project.media_files.map((file, idx) =>
                isImage(file.filename) ? (
                  <img
                    key={idx}
                    src={fileUrl(file.filename)}
                    alt=""
                    style={{ width: 80, height: 80, objectFit: "cover", margin: 4, borderRadius: 4 }}
                  />
                ) : (
                  <a key={idx} href={fileUrl(file.filename)} download>
                    {file.filename}
                  </a>
                )
              )}
            </div>
          )}
        </div>
      )}

      <button style={{ marginTop: 6 }} onClick={() => deleteProject(project.id)}>
        ðŸ—‘ Delete
      </button>
    </div>
  );
}
