import React, { useEffect, useState } from "react";
import SkillForm from "./SkillForm";
import SkillCategory from "./SkillCategory";
import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
  DragOverlay,
} from "@dnd-kit/core";
import { arrayMove } from "@dnd-kit/sortable";

const API_URL = process.env.REACT_APP_API_URL + "/skills";
const USER_ID = process.env.REACT_APP_USER_ID;

export default function SkillList() {
  const [skills, setSkills] = useState([]);
  const [activeId, setActiveId] = useState(null);

  const sensors = useSensors(useSensor(PointerSensor));

  // --- Load skills ---
  useEffect(() => {
    fetch(`${API_URL}?user_id=${USER_ID}`)
      .then(res => res.json())
      .then(data => setSkills(data));
  }, []);

  // --- Add / Update / Remove skill ---
  const addSkill = async (skill) => {
    if (skills.some(s => s.name.toLowerCase() === skill.name.toLowerCase() && s.category === skill.category)) {
      return alert("Skill already exists in this category");
    }
    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...skill, user_id: USER_ID })
      });
      const addedSkill = await res.json();
      setSkills([...skills, addedSkill]);
    } catch (err) {
      console.error(err);
    }
  };

  const updateSkill = async (id, updatedFields) => {
    await fetch(`${API_URL}/${id}?user_id=${USER_ID}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedFields)
    });
    setSkills(skills.map(s => (s.id === id ? { ...s, ...updatedFields } : s)));
  };

  const removeSkill = async (id) => {
    if (!window.confirm("Remove this skill?")) return;
    await fetch(`${API_URL}/${id}?user_id=${USER_ID}`, { method: "DELETE" });
    setSkills(skills.filter(s => s.id !== id));
  };

  // --- Drag handlers ---
  const handleDragStart = (event) => {
    setActiveId(event.active.id);
  };

  const handleDragEnd = async (event) => {
    const { active, over } = event;
    setActiveId(null);
    if (!over || active.id === over.id) return;

    const activeSkill = skills.find(s => s.id === active.id);
    const overSkill = skills.find(s => s.id === over.id);

    if (!activeSkill || !overSkill) return;

    let newSkills = [...skills];

    // --- Moving within same category ---
    if (activeSkill.category === overSkill.category) {
      const catSkills = newSkills
        .filter(s => s.category === activeSkill.category)
        .sort((a,b) => a.position - b.position);
      const oldIndex = catSkills.findIndex(s => s.id === active.id);
      const newIndex = catSkills.findIndex(s => s.id === over.id);
      const reordered = arrayMove(catSkills, oldIndex, newIndex);

      reordered.forEach((s, idx) => s.position = idx);
      newSkills = [
        ...newSkills.filter(s => s.category !== activeSkill.category),
        ...reordered
      ];
      setSkills(newSkills);

      // Update backend positions
      for (let s of reordered) {
        await updateSkill(s.id, { position: s.position });
      }

    } else {
      // --- Moving across categories ---
      const sourceCat = activeSkill.category;
      const targetCat = overSkill.category;

      const sourceItems = newSkills
        .filter(s => s.category === sourceCat && s.id !== active.id)
        .sort((a,b) => a.position - b.position);
      const targetItems = newSkills
        .filter(s => s.category === targetCat)
        .sort((a,b) => a.position - b.position);

      const overIndex = targetItems.findIndex(s => s.id === over.id);
      const movedSkill = { ...activeSkill, category: targetCat };

      const newTargetItems = [...targetItems];
      newTargetItems.splice(overIndex, 0, movedSkill);

      // Reassign positions
      sourceItems.forEach((s, idx) => s.position = idx);
      newTargetItems.forEach((s, idx) => s.position = idx);

      newSkills = [
        ...newSkills.filter(s => s.category !== sourceCat && s.category !== targetCat),
        ...sourceItems,
        ...newTargetItems
      ];

      setSkills(newSkills);

      // Update backend
      for (let s of [...sourceItems, ...newTargetItems]) {
        await updateSkill(s.id, { category: s.category, position: s.position });
      }
    }
  };

  const categories = ["Technical","Soft Skills","Languages","Industry-Specific"];

  return (
    <div>
      <h2>Add Skill</h2>
      <SkillForm addSkill={addSkill} existingSkills={skills} />

      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
      >
        {categories.map(cat => (
          <SkillCategory
            key={cat}
            category={cat}
            skills={skills
              .filter(s => s.category === cat)
              .sort((a,b) => a.position - b.position)}
            updateSkill={updateSkill}
            removeSkill={removeSkill}
          />
        ))}

        <DragOverlay>
          {activeId ? (
            <div style={{
              padding: "6px 10px",
              background: "#ccc",
              borderRadius: "8px"
            }}>
              {skills.find(s => s.id === activeId)?.name}
            </div>
          ) : null}
        </DragOverlay>
      </DndContext>
    </div>
  );
}
