import React from "react";
import { useSortable } from "@dnd-kit/sortable";

// Category color + emoji mapping
const categoryMeta = {
  Technical: { color: "#4f8ef7", emoji: "üíª" },
  "Soft Skills": { color: "#34c759", emoji: "üó£Ô∏è" },
  Languages: { color: "#ff9500", emoji: "üàØ" },
  "Industry-Specific": { color: "#af52de", emoji: "üè≠" }
};

export default function SkillItem({ skill, updateSkill, removeSkill }) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: skill.id });

  const meta = categoryMeta[skill.category] || { color: "#ccc", emoji: "" };
  const style = {
    display: "flex",
    gap: "8px",
    alignItems: "center",
    padding: "6px 10px",
    borderRadius: "8px",
    backgroundColor: meta.color + "33",
    marginBottom: "4px",
    width: "100%",
    maxWidth: "250px",
    transform: transform ? `translate3d(${transform.x}px, ${transform.y}px, 0)` : undefined,
    transition,
  };

  return (
    <div ref={setNodeRef} style={style}>
      {/* Only the name span is draggable */}
      <span {...attributes} {...listeners} style={{ flexGrow: 1, cursor: "grab" }}>
        {meta.emoji} <strong>{skill.name}</strong>
      </span>

      <select
        value={skill.proficiency}
        onChange={(e) => updateSkill(skill.id, { ...skill, proficiency: e.target.value })}
      >
        <option>Beginner</option>
        <option>Intermediate</option>
        <option>Advanced</option>
        <option>Expert</option>
      </select>

      {/* Trash button is outside draggable area */}
      <button
        onClick={() => removeSkill(skill.id)}
        style={{ pointerEvents: "auto", cursor: "pointer" }}
      >
        üóë
      </button>
    </div>
  );
}
