import React from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import SkillItem from "./SkillItem";

export function SortableItem({ skill, updateSkill, removeSkill, isHidden }) {
  const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id: skill.id });

  // Hide dragged item except placeholder
  if (isHidden && !skill.id.toString().includes("placeholder")) return null;

  const style = {
    transform: transform ? CSS.Transform.toString(transform) : undefined,
    transition,
    marginBottom: "5px",
    minHeight: skill.id.toString().includes("placeholder") ? "40px" : undefined,
  };

  return (
    <li ref={setNodeRef} style={style} {...attributes} {...listeners}>
      {!skill.id.toString().includes("placeholder") && (
        <SkillItem
          skill={skill}
          updateSkill={updateSkill}
          removeSkill={removeSkill}
          listeners={listeners}
          attributes={attributes}
          setNodeRef={setNodeRef}
          transform={transform}
          transition={transition}
        />
      )}
    </li>
  );
}
