import React, { useEffect, useState } from "react";
import SkillForm from "./SkillForm";
import SkillCategory from "./SkillCategory";
import { DndContext, closestCenter, PointerSensor, useSensor, useSensors, DragOverlay } from "@dnd-kit/core";
import { arrayMove } from "@dnd-kit/sortable";
import SkillItem from "./SkillItem";

const API_URL = process.env.REACT_APP_API_URL + "/skills";
const USER_ID = process.env.REACT_APP_USER_ID;

export default function SkillList() {
  const [skills, setSkills] = useState([]);
  const [activeId, setActiveId] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");

  const sensors = useSensors(useSensor(PointerSensor));

  useEffect(() => {
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      const res = await fetch(`${API_URL}?user_id=${USER_ID}`);
      const data = await res.json();
      setSkills(data);
    } catch (err) {
      console.error("Fetch failed:", err);
    }
  };

  const addSkill = async (skill) => {
    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...skill, user_id: USER_ID }),
      });
      const addedSkill = await res.json();
      fetchSkills(); // refresh list
    } catch (err) {
      console.error(err);
    }
  };

  const updateSkill = async (id, updatedFields) => {
    try {
      await fetch(`${API_URL}/${id}?user_id=${USER_ID}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedFields),
      });
      fetchSkills(); // refresh list
    } catch (err) {
      console.error(err);
    }
  };

  const removeSkill = async (id) => {
    if (!window.confirm("Remove this skill?")) return;
    try {
      await fetch(`${API_URL}/${id}?user_id=${USER_ID}`, { method: "DELETE" });
      fetchSkills(); // refresh list
    } catch (err) {
      console.error(err);
    }
  };

  const handleDragStart = (event) => {
    setActiveId(event.active.id);
  };

  const handleDragEnd = async (event) => {
    const { active, over } = event;
    setActiveId(null);
    if (!active || !over) return;

    const activeSkill = skills.find(s => s.id === active.id);
    if (!activeSkill) return;

    const overCategory = over.id.startsWith("empty-") ? over.id.replace("empty-", "") : skills.find(s => s.id === over.id)?.category;
    if (!overCategory) return;

    // Update backend directly
    await updateSkill(activeSkill.id, { category: overCategory });

    // Refetch entire list to normalize positions & allow empty drops
    fetchSkills();
  };

  const activeSkillObj = skills.find(s => s.id === activeId);

  // Global search
  const filteredSkills = skills.filter(s => s.name.toLowerCase().includes(searchTerm.toLowerCase()));

  return (
    <div>
      <h2>Add Skill</h2>
      <SkillForm addSkill={addSkill} existingSkills={skills} />

      <div style={{ marginTop: "20px", marginBottom: "20px" }}>
        <input
          placeholder="Search skills globally..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <DndContext
        sensors={sensors}
        collisionDetection={closestCenter}
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
      >
        {["Technical", "Soft Skills", "Languages", "Industry-Specific"].map(cat => (
          <SkillCategory
            key={cat}
            category={cat}
            skills={filteredSkills.filter(s => s.category === cat).sort((a, b) => a.position - b.position)}
            updateSkill={updateSkill}
            removeSkill={removeSkill}
          />
        ))}

        <DragOverlay>
          {activeSkillObj && (
            <SkillItem
              skill={activeSkillObj}
              updateSkill={updateSkill}
              removeSkill={removeSkill}
            />
          )}
        </DragOverlay>
      </DndContext>
    </div>
  );
}
